Trigger.script:
if string.lower(matches[2]) ~= targetName
  then
    playSoundFile({
      name = "super-mario-bros-coin.mp3"
      , volume = 10
  })
  alert();
end

--deleteLine();
chat_color = "<purple>";
cecho(LW.chatBox.name, "\n" .. getTime(true, "h:mmAP ") .. chat_color .. matches[2]);
chat_color = "<white>";
cecho(LW.chatBox.name, chat_color .. matches[3]);

--\ \<.*\>\

chat_color = "<orange>";
cecho(LW.chatBox.name, "\n" .. getTime(true, "h:mmAP ") .. chat_color .. matches[2]);
if matches[3]
  then
    chat_color = "<white>";
    cecho(LW.chatBox.name, chat_color .. matches[3]);
end

--deleteLine();
chat_color = "<red>";
cecho(LW.chatBox.name, "\n" .. getTime(true, "h:mmAP ") .. chat_color .. matches[2] .. " " .. matches[3]);

--deleteLine();
chat_color = "<red>";
cecho(LW.chatBox.name, "\n" .. getTime(true, "h:mmAP ") .. chat_color .. "<<" .. matches[2] .. ":");
chat_color = "<white>";
cecho(LW.chatBox.name, chat_color .. matches[3]);

--deleteLine();
chat_color = "<red>";
cecho(LW.chatBox.name, "\n" .. getTime(true, "h:mmAP ") .. chat_color .. ">>" .. matches[2]);
chat_color = "<white>";
cecho(LW.chatBox.name, chat_color .. matches[3]);
